import { authAPI } from "./api";

const SET_USER_DATA = 'SET_USER_DATA';



let initialState = {
    userId: null,
    email: null,
    login: null,
    isAuth: false
    
};

const authReducer = (state = initialState, action) => {
    switch(action.type) {
        case SET_USER_DATA:
            return {
                ...state,
                ...action.payload,
            }

            default:
                return state;
    }
}

export const setAuthUserData = (userId, email, login, isAuth) => ({type:SET_USER_DATA, payload: 
    {userId,email,login, isAuth}})
export const getAuthUserData = () => (dispatch) => {
    authAPI.me()
    .then(response => {
        if (response.data.resultCode ===0) {
            let {id,login,email} = response.data.data;
            dispatch(setAuthUserData(id, email, login, true));
        }
    })
}

export const login = (email,password,rememberMe) => (dispatch) => {
    authAPI.login(email, password, rememberMe)
    .then(response => {
        if (response.data.resultCode === 0) {
            dispatch(getAuthUserData())
        }
        if (response.data.resultCode === Admin)
        {
            dispatch(getAuthUserData())
        }
    })
}

export const logout = (email,password,rememberMe) => (dispatch) => {
    authAPI.logout(email, password, rememberMe)
    .then(response => {
        if (response.data.resultCode ===0) {
            dispatch(setAuthUserData(null, null, null, false))
        }
    })
}



export default authReducer;
